@languages:
{{#exists languages data="language"}}
  <ul class="languages">
    {{#each items}}
      <li class="language-item">
        <a href="{{link}}" title="{{name}}" aria-current="{{aria}}">{{name}}</a>
      </li>
    {{/each}}
  </ul>
{{/exists}}

@coming-soon:
{{#exists coming-soon data='coming-soon'}}
  <div class="coming-soon">
    {{> @image}}
    <h1 class="title">{{ title }}</h1>
    <p class="text">{{ text }}</p>
  </div>
{{/exists}}

@_brand:
{{#exists brand data="brand"}}
  <a class="brand" href="{{url}}">
    {{#if image}}
      <img src="{{imageSource image.source}}" alt="{{image.description}}" />
    {{/if}}
    {{#if name}}<span>{{name}}</span>{{/if}}
  </a>
{{/exists}}

@image:
{{#exists image data="image"}}
  <figure class="image">
    <img class="lazyload" src="{{blured name}}" data-src="{{imageSource name}}" alt="{{description}}" />
  </figure>
{{/exists}}

@typewriter:
{{#exists typewriter data='typewriter'}}
  <p class="typewriter" data-text="{{ text }}" {{#if caret}}data-caret="{{caret}}"{{/if}}>{{ text }}</p>
{{/exists}}
