@author:
{{#with author}}
  <p class="author">
    {{ text }}
    <a target="_blank" href="{{ link }}">{{ name }}</a>
  </p>
{{else}}
  {{> @data-not-found data='author'}}
{{/with}}

@brand:
{{#with brand}}
  {{#if image}}
    <a class="brand" href="{{ link }}">
      <img src="{{source image.name}}" alt="{{image.description}}" />
    </a>
  {{else}}
    <a class="brand" href="{{ link }}">
      <span>{{name}}</span>
    </a>
  {{/if}}
{{else}}
  {{> @data-not-found data='brand'}}
{{/with}}

@copyright:
{{#with copyright}}
<p class="copyright">
  {{ year }}
  {{#if link}}
    <a target="_blank" href="{{ link }}">
      {{ name }}
    </a>
  {{else}}
    {{ name }}
  {{/if}}
</p>
{{else}}
  {{> @data-not-found data='copyright'}}
{{/with}}

@image-base:
{{#with image }}
<figure class="image base">
  <img src="{{source name}}" alt="{{description}}" />
</figure>
{{else }}
  {{> @data-not-found data='image'}}
{{/with}}

@image-responsive:
{{#with image }}
<figure class="image responsive">
  <img
    class="lazyload"
    src="{{blured name}}"
    data-srcset="{{sourceSet name}}"
    data-sizes="auto"
    alt="{{ description }}"
  />
</figure>
{{else }}
  {{> @data-not-found data='image'}}
{{/with}}

@image-simple:
{{#with image }}
<figure class="image simple">
  <img class="lazyload" src="{{blured name}}" data-src="{{source name}}" alt="{{description}}" />
</figure>
{{else }}
  {{> @data-not-found data='image'}}
{{/with}}

@typewriter:
{{#with typewriter}}
  <p class="typewriter" data-text="{{ text }}" {{#if caret}}data-caret="{{caret}}"{{/if}}>{{ text }}</p>
{{else}}
  {{> @data-not-found data='typewriter'}}
{{/with}}

@modal:
{{#if modal}}
  {{#with modal}}
    <div class="modal" id="{{id}}">
      <div class="modal-content">
        <div class="modal-header">
          <h2>{{title}}</h2>
          <a href="#" class="modal-close">&times;</a>
        </div>
        <div class="modal-body">
          {{{content}}}
        </div>
        <div class="modal-footer">
          {{#if buttons}}
            <div class="modal-buttons">
              {{#each buttons}}
                <a href="#" class="button {{#if class}}{{class}}{{else}}button-primary{{/if}}">{{text}}</a>
              {{/each}}
            </div>
          {{/if}}
        </div>
      </div>
    </div>
  {{/with}}
{{else}}
  {{> @data-not-found data='modal'}}
 {{/if}}

@accordion:
{{#with accordion}}
  <div class="accordion">
    <div class="accordion-header">
      {{#if image}}{{> @image-responsive}}{{/if}}
      <div class="heading">
        <h4 class="title">{{title}}</h4>
        {{#if subtitle}}
          <p class="subtitle">{{subtitle}}</p>
        {{/if}}
      </div>
      <button class="toggle">{{toggleLabel}}</button>
    </div>
    <div class="accordion-content">
      {{{content}}}
      <div class="accordion-footer">
        <button class="close">{{closeLabel}}</button>
      </div>
    </div>
  </div>
{{else}}
  {{> @data-not-found data='accordion'}}
{{/with}}

@address:
{{#with address}}
  <address class="address">
    <h6 class="name">{{name}}</h6>
    <p class="line">{{street}}</p>
    <p class="line">{{zip}} {{city}}</p>
    {{#if country}}<p class="line">{{country}}</p>{{/if}}
    {{#each phone}}
      <a class="phone" href="tel:{{this}}">{{this}}</a>
    {{/each}}
    <a class="email" href="mailto:{{email}}">{{email}}</a>
  </address>
{{else}}
  {{> @data-not-found data='address'}}
{{/with}}

@gallery:
{{#if gallery }}
  <div class="gallery">
    {{#each gallery.items }}
      <div class="item">
        {{> @image-responsive}}
        <div class="info">
          <h4 class="name">{{ name }}</h4>
          <p class="description">{{ description }}</p>
        </div>
      </div>
    {{/each }}
  </div>
</div>
{{else }}
  {{> @data-not-found data='gallery'}}
{{/if }}

@language:
{{#with language }}
  <ul class="language">
    {{#each items }}
      <li class="language-item">
        <a href="{{ link }}" title="{{ name }}" aria-current="{{ aria }}">{{ name }}</a>
      </li>
    {{/each }}
  </ul>
{{else }}
  {{> @data-not-found data='language'}}
{{/with }}

@menu:
{{#with menu }}
  <ul class="menu" role="navigation">
    {{#each items}}
      <li class="menu-item">
        <a href="{{ link }}" title="{{ name }}">
          <span>{{ name }}</span>
        </a>
      </li>
    {{/each }}
  </ul>
{{else}}
  {{> @data-not-found data='menu'}}
{{/with}}

@social-networks:
{{#if socialNetworks}}
  <div class="social-networks">
    <ul>
      {{#each socialNetworks.items}}
        <li class="item">
          <a href="{{ link }}" title="{{ name }}">
            <span>{{ name }}</span>
          </a>
        </li>
      {{/each}}
    </ul>
  </div>
{{else}}
  {{> @data-not-found data='socialNetworks'}}
{{/if}}

@wordcloud:
{{#if wordcloud }}
<ul class="wordcloud">
  {{#each wordcloud.items}}
  <li class="item value-{{ value }}" title="{{ description }}">{{ name }}</li>
  {{/each}}
</ul>
{{else}}
  {{> @data-not-found data='wordcloud'}}
{{/if}}

@bildraum-footer:
{{#with footer}}
  <footer id="{{id}}">
    <div class="contact">
      {{#with address}}
        {{#each this}}
          {{> @address address=this}}
        {{/each}}
      {{/with}}
      <a class="impressum" href="/impressum">impressum</a>
    </div>
    {{> @copyright}}
    {{> @author}}
  </footer>
{{else }}
  {{> @data-not-found data='image'}}
{{/with}}

@section-about:
{{#with sectionAbout }}
  <div class="section-about">
    <h2 class="title">{{ title }}</h2>
    <h3 class="subtitle">{{ subtitle }}</h3>
    <p>{{text}}</p>
    <div class="content">{{{ content }}}</div>
  </div>
{{else}}
  {{> @data-not-found data='sectionAbout'}}
{{/with}}

@section-content:
{{#with sectionContent }}
  <section id="{{id}}" class="section-content">
    <header class="heading">
      <h2 class="title">{{ title }}</h2>
    </header>
    <div class="content">
      {{{ content }}}
    </div>
  </section>
{{else}}
  {{> @data-not-found data='sectionContent'}}
{{/with}}

@section-empty:
{{#if sectionEmpty }}
  {{#with sectionEmpty }}
      <section id="{{id}}" class="section-empty">
        <h2 class="title">{{ title }}</h2>
        <div class="content">
          <p>{{{ text }}}</p>
        </div>
      </section>
  {{/with}}
{{else}}
  {{> @data-not-found data='sectionEmpty'}}
{{/if}}

@section-image:
{{#with sectionImage}}
  <section id="{{id}}" class="section-image">
    <header class="heading">
      <h2 class="title">{{ title }}</h2>
    </header>
    <div class="content">
      {{> @image-responsive}}
    </div>
  </section>
{{else}}
  {{> @data-not-found data='sectionImage'}}
{{/with}}

@section-portfolio:
{{#if sectionPortfolio }}
  {{#with sectionPortfolio }}
    <section id="id" class="section-portfolio">
      <h2 class="title">{{ title }}</h2>
      {{> @gallery }}
    </section>
  {{/with }}
{{else}}
  {{> @data-not-found data='sectionPortfolio'}}
{{/if}}

@section-skills:
{{#if sectionSkills }}
  {{#with sectionSkills }}
    <section class="section-skills">
      <h2 class="title">{{ title }}</h2>
      {{#if wordcloud }} {{>wordcloud  }} {{/if }}
    </section>
  {{/with }}
{{else}}
  {{> @data-not-found data='sectionSkills'}}
{{/if}}

@coming-soon:
{{#if comingSoon }}
  {{#with comingSoon }}
    <div class="coming-soon">
      {{> @image-simple}}
      <h1 class="title">{{ title }}</h1>
      <p class="text">{{ text }}</p>
    </div>
  {{/with}}
{{else}}
  {{> @data-not-found data='comingSoon'}}
{{/if}}

@hero:
{{#if hero }}
  {{#with hero}}
    <div class="hero">
      {{#if image}} {{> @image-responsive}} {{/if}}
      <div class="cover"></div>
      <div class="content">
        {{#if pretitle}}<p class="pretitle">{{ pretitle }}</p>{{/if}}
        <h1 class="title">{{{ title }}}</h1>
        {{#if subtitle}} <p class="subtitle">{{subtitle}}</p> {{/if}}
        {{#if typewriter}} {{> @typewriter }} {{/if}}
        {{#if socialNetworks}} {{> @social-networks }} {{/if}}
      </div>
    </div>
  {{/with }}
{{else }}
  {{> @data-not-found data='hero'}}
{{/if }}
